import "./App.css";
import {
    Route,
    BrowserRouter as Router,
    Switch,
    Redirect,
} from "react-router-dom";
import React, { useContext, useState } from "react";
import AuthApi from "./context/AuthApi";
import Cookies from "js-cookie";
import axios from "axios";
const Login = () => {
    let fetcher = Cookies.get("xxx");
    console.log(fetcher);
    const Auth = useContext(AuthApi);
    const handleLogin = () => {
        Auth.setAuth(true);
    };
    return (
        <form className="container" onSubmit={handleLogin}>
            <div className="row justify-content-center my-5 mt-5 pt-5">
                <div className="col-md-4 shadow my-auto">
                    <h4 className="my-4">Login Form</h4>
                    <div className="form-group">
                        <input
                            type="email"
                            className="form-control"
                            placeholder="Email Address"
                            // required
                            name="email"
                            value={inputs.email}
                            onChange={handleInputChange}
                        />
                    </div>
                    <div className="form-group">
                        <input
                            type="password"
                            className="form-control"
                            placeholder="Password.."
                            // required
                            name="password"
                            value={inputs.password}
                            onChange={handleInputChange}
                        />
                    </div>
                    <div className="form-group">
                        <button className="w-100 btn btn-lg btn-primary">
                            Sign In
                        </button>
                    </div>

                    <div className="px-2 font-bold py-2 ">
                        <NavLink to="/register" className="font-weight-bold">
                            Register
                        </NavLink>
                    </div>
                </div>
            </div>
            <Social />
        </form>
    );
};
const Dashboard = () => {
    let fetcher = axios({
        method: "get",
        url: "/api/user",
        withCredentials: true,
    });
    console.log(fetcher.data);
    return (
        <div>
            <h3>Dashboard</h3>
            <button>Dashboard</button>
        </div>
    );
};
function App() {
    const [auth, setAuth] = useState(false);
    return (
        <>
            <AuthApi.Provider value={{ auth, setAuth }}>
                <Router>
                    <Routes />
                </Router>
            </AuthApi.Provider>
        </>
    );
}
const Routes = () => {
    const Auth = useContext(AuthApi);
    return (
        <Switch>
            <ProtectedLogin path="/login" auth={Auth.auth} component={Login} />

            <ProtectedRoute
                path="/dashboard"
                auth={Auth.auth}
                component={Dashboard}
            />
        </Switch>
    );
};
const ProtectedRoute = ({ auth, component: Component, ...rest }) => {
    return (
        <Route
            {...rest}
            render={() => (auth ? <Component /> : <Redirect to="/login" />)}
        />
    );
};
const ProtectedLogin = ({ auth, component: Component, ...rest }) => {
    return (
        <Route
            {...rest}
            render={() =>
                !auth ? <Component /> : <Redirect to="/dashboard" />
            }
        />
    );
};
export default App;
