import axios from "axios";
import { useContext, useState } from "react";
import { NavLink, Redirect } from "react-router-dom";
import AuthApi from "./context/AuthApi";
import useSignUpForm from "./CustomHooks";
import Social from "./Social";
import Cookies, { set } from "js-cookie";
const Login = () => {
    const Auth = useContext(AuthApi);
    const [redirect, setRedirect] = useState(false);
    const handleLogin = async (e) => {
        e.preventDefault();

        const { email, password } = inputs;
        await axios({
            method: "post",
            url: "/api/login",
            data: {
                email,
                password,
            },
        });
        setRedirect(true);
        if (redirect) {
           Auth.set
            return <Redirect to="/admin" />;
        }
    };
    const { inputs, handleInputChange } = useSignUpForm();
    return (
        <form className="container" onSubmit={handleLogin}>
            <div className="row justify-content-center my-5 mt-5 pt-5">
                <div className="col-md-4 shadow my-auto">
                    <h4 className="my-4">Login Form</h4>
                    <div className="form-group">
                        <input
                            type="email"
                            className="form-control"
                            placeholder="Email Address"
                            // required
                            name="email"
                            value={inputs.email}
                            onChange={handleInputChange}
                        />
                    </div>
                    <div className="form-group">
                        <input
                            type="password"
                            className="form-control"
                            placeholder="Password.."
                            // required
                            name="password"
                            value={inputs.password}
                            onChange={handleInputChange}
                        />
                    </div>
                    <div className="form-group">
                        <button className="w-100 btn btn-lg btn-primary">
                            Sign In
                        </button>
                    </div>

                    <div className="px-2 font-bold py-2 ">
                        <NavLink to="/register" className="font-weight-bold">
                            Register
                        </NavLink>
                    </div>
                </div>
            </div>
            <Social />
        </form>
    );
};
export default Login;
