import { useState } from "react";

const useSignUpForm = () => {
    const [inputs, setInputs] = useState({
        firstname: "",
        lastname: "",
        email: "",
        password: "",
    });
    const handleRegister = (event) => {
        if (event) {
            event.preventDefault();
            console.log(inputs);
        }
    };
    const handleInputChange = (event) => {
        console.log("xx");
        event.persist();
        setInputs((inputs) => ({
            ...inputs,
            [event.target.name]: event.target.value,
        }));
    };
    return {
        handleSubmit,
        handleInputChange,
        inputs,
    };
};
export default useSignUpForm;
